import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const BoysSchoolMessage = () => {
  return (
    <div className="container-fluid bg-white position-relative">
      {/* Login Ribbon */}
      <div className="position-absolute top-0 end-0 m-3">
        <div className="bg-danger text-white py-2 px-4 rounded-pill">
          LOGIN
        </div>
      </div>

      {/* Main Content */}
      <div className="row align-items-center py-5">
        {/* Left Side - Text Section */}
        <div className="col-md-6 text-center text-md-start">
          <h2 className="text-primary fw-bold mb-3">WHY A BOYS' SCHOOL</h2>
          <p className="mb-3">
            Both single-sex and co-educational secondary schools offer high-quality education. 
            When choosing between these options, your decision should be guided by your child's 
            individual needs.
          </p>
          <p className="mb-4">
            Our guide explores the five key benefits of choosing a boysâ€™ school.
          </p>
          <a href="#" className="btn btn-danger text-white text-uppercase">
            Download the PDF
          </a>
        </div>

        {/* Right Side - Image Section */}
        <div className="col-md-6">
          <img 
            src="https://via.placeholder.com/600x400" // Replace with actual image source
            alt="Students studying" 
            className="img-fluid rounded"
          />
        </div>
      </div>
    </div>
  );
};

export default BoysSchoolMessage;
