import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import AboutUsStyle from "../css/AboutUsStyle.css"

const AboutUs = () => {
  const contentArray = [
    {
      topheading: "About us",
      title: "The Strategic Plan",
      subtitle: "2022-2026",
      buttonText: "Download PDF",
      buttonLink: "#",
    },
    {
      topheading: "About us",
      title: "The Salesian",
      subtitle: "Difference",
      buttonText: "LEARN MORE",
      buttonLink: "#",
    },
    {
      topheading: "About us",
      title: "Explore Our",
      subtitle: "History",
      buttonText: "LEARN MORE",
      buttonLink: "#",
    },
    {
      topheading: "About us",
      title: "Download",
      subtitle: "Our Prospectus",
      buttonText: "Download PDF",
      buttonLink: "#",
    },
    {
      topheading: "About us",
      title: "Learn About Our",
      subtitle: "Enrichment Programs",
      buttonText: "LEARN MORE",
      buttonLink: "#",
    },
  ];

  const imageArray = [
    "https://images.pexels.com/photos/8617899/pexels-photo-8617899.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    "https://images.pexels.com/photos/8617965/pexels-photo-8617965.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    "https://images.pexels.com/photos/8199610/pexels-photo-8199610.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    "https://images.pexels.com/photos/10638070/pexels-photo-10638070.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    "https://images.pexels.com/photos/4936021/pexels-photo-4936021.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % contentArray.length);
    }, 2000);
    return () => clearInterval(interval);
  }, [contentArray.length]);

  return (
    <div className="container-fluid p-0 vh-100 d-flex align-items-center">
      {/* Row Container */}
      <div className="row w-100 mx-0">
        {/* Left Side - Text Content */}
        <div className="col-12 col-md-6 d-flex flex-column border justify-content-center align-items-center align-items-md-start order-2 order-md-1 text-center text-md-start px-5">
          {/* Top Heading */}
       

        </div>
        {/* Right Side - Full Cover Image */}
        <motion.div
          className="col-12 col-md-6 order-1 order-md-2 p-0 d-flex justify-content-center align-items-center"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 0.4 }}
          key={currentIndex + "-image"}
          style={{ height: "100vh" }} // Ensure full height for image container
        >
          <img
            src={imageArray[currentIndex]}
            alt="College and Students"
            className="img-fluid w-100 h-100"
            style={{
              objectFit: "cover", // Cover the entire div
              borderTopLeftRadius: "10px",
              borderBottomLeftRadius: "10px",
            }} // Optional rounded corners for a sleeker look
          />
        </motion.div>
      </div>
    </div>
  );
};

export default AboutUs;
