import React from 'react';
import "../css/About.css";
import img2 from "../images/college_photo_2.jpg";
import img3 from "../images/college_photo_3.jpg";
import img4 from "../images/college_photo_4.jpg";

const About = () => {
    return (
        <>
            <div className="d-flex">
                <div className="carousel-left p-5" style={{ flex: '1', backgroundColor: '#f8f9fa' }}>
                    <h6>LEARNING</h6>
                    <h2>Learn about our <br /><strong>ENRICHMENT PROGRAMS</strong></h2>
                    <button className="btn btn-danger mt-3">LEARN MORE</button>
                </div>
                <div className="carousel-right" style={{ flex: '1', backgroundImage: 'url(https://via.placeholder.com/600x400)', backgroundSize: 'cover' }}>
                    {/* You can replace the placeholder URL with your image path */}
                </div>
            </div>


            {/* You can add more Carousel.Items if you want additional slides */}
        </>
    );
}

export default About;
